    public function getSourceContext(): Source
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Your Cart{% endblock %}

{% block body %}
    <h1>Your Cart</h1>

    {% if items is not empty %}
        <table class=\"table\">
            <thead>
                <tr>
                    <th>Product</th>
                    <th>Quantity</th>
                    <th>Price</th>
                    <th>Total</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                {% for item in items %}
                    <tr>
                        <td>{{ item.product.name }}</td>
                        <td>{{ item.quantity }}</td>
                        <td>{{ item.product.price }} €</td>
                        <td>{{ item.product.price * item.quantity }} €</td>
                        <td>
                            <a href=\"{{ path('app_cart_remove', {'id': item.product.id}) }}\" class=\"btn btn-danger\">Remove</a>
                        </td>
                    </tr>
                {% endfor %}
            </tbody>
        </table>

        <h3>Total: {{ total }} €</h3>
        <a href=\"{{ path('checkout') }}\" class=\"btn btn-primary\">Proceed to Checkout</a>
    {% else %}
        <p>Your cart is empty.</p>
    {% endif %}
{% endblock %}
", "cart/index.html.twig", "/home/viaansh/shop/templates/cart/index.html.twig");
    }
